import React, { useState } from "react";
import ProductsManagement from "./ProductsManagement";
import CategoriesManagement from "./CategoriesManagement";
import SocialLinksManagement from "./SocialLinksManagement";
import SettingsManagement from "./SettingsManagement";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("settings");

  const renderContent = () => {
    switch (activeTab) {
      case "products":
        return <ProductsManagement />;
      case "categories":
        return <CategoriesManagement />;
      case "social":
        return <SocialLinksManagement />;
      case "settings":
      default:
        return <SettingsManagement />;
    }
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      {/* القائمة الجانبية */}
      <aside
        style={{
          width: "220px",
          background: "#222",
          color: "white",
          padding: "20px",
        }}
      >
        <h2 style={{ marginBottom: "20px" }}>لوحة التحكم</h2>
        <ul style={{ listStyle: "none", padding: 0 }}>
          <li>
            <button
              onClick={() => setActiveTab("settings")}
              style={{
                background: activeTab === "settings" ? "#444" : "transparent",
                color: "white",
                border: "none",
                padding: "10px",
                width: "100%",
                textAlign: "left",
                cursor: "pointer",
              }}
            >
              ⚙️ إعدادات المتجر
            </button>
          </li>
          <li>
            <button
              onClick={() => setActiveTab("social")}
              style={{
                background: activeTab === "social" ? "#444" : "transparent",
                color: "white",
                border: "none",
                padding: "10px",
                width: "100%",
                textAlign: "left",
                cursor: "pointer",
              }}
            >
              🌐 روابط التواصل
            </button>
          </li>
          <li>
            <button
              onClick={() => setActiveTab("products")}
              style={{
                background: activeTab === "products" ? "#444" : "transparent",
                color: "white",
                border: "none",
                padding: "10px",
                width: "100%",
                textAlign: "left",
                cursor: "pointer",
              }}
            >
              📦 إدارة المنتجات
            </button>
          </li>
          <li>
            <button
              onClick={() => setActiveTab("categories")}
              style={{
                background: activeTab === "categories" ? "#444" : "transparent",
                color: "white",
                border: "none",
                padding: "10px",
                width: "100%",
                textAlign: "left",
                cursor: "pointer",
              }}
            >
              🗂️ إدارة الأقسام
            </button>
          </li>
        </ul>
      </aside>

      {/* المحتوى الرئيسي */}
      <main style={{ flex: 1, padding: "20px" }}>{renderContent()}</main>
    </div>
  );
};

export default AdminDashboard;
