  useEffect(() => {
    const fetchLinks = async () => {
      const { data, error } = await supabase.from("social_links").select("*").single();
      if (!error && data) setLinks(data);
    };
    fetchLinks();
  }, []);

  return (
    <div className="p-6 bg-pink-50 rounded-xl shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-pink-600">ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß</h2>
      <div className="flex gap-6 text-lg">
        <a
          href={links.facebook}
          target="_blank"
          rel="noreferrer"
