import React, { useEffect, useState } from "react";
import supabase from "../../utils/supabase";

const OrdersManagement = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    const { data } = await supabase.from("orders").select("*");
    setOrders(data || []);
  };

  const handleStatusChange = async (id, status) => {
    await supabase.from("orders").update({ status }).eq("id", id);
    fetchOrders();
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">üì¶ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™</h2>
      <div className="space-y-2">
        {orders.map((o) => (
          <div key={o.id} className="border p-2 rounded bg-white flex justify-between items-center">
            <div>
              <p><span className="font-bold">ÿßŸÑÿßÿ≥ŸÖ:</span> {o.customer_name}</p>
              <p><span className="font-bold">ÿßŸÑŸáÿßÿ™ŸÅ:</span> {o.phone}</p>
              <p><span className="font-bold">ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä:</span> {o.total} ÿ¨ŸÜŸäŸá</p>
              <p><span className="font-bold">ÿßŸÑÿ≠ÿßŸÑÿ©:</span> {o.status}</p>
            </div>
            <div className="space-x-2">
              <button onClick={() => handleStatusChange(o.id, "ŸÇŸäÿØ ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©")} className="bg-yellow-500 text-white px-3 py-1 rounded">ŸÇŸäÿØ ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©</button>
              <button onClick={() => handleStatusChange(o.id, "ÿ™ŸÖ ÿßŸÑÿ™ŸàÿµŸäŸÑ")} className="bg-green-600 text-white px-3 py-1 rounded">ÿ™ŸÖ ÿßŸÑÿ™ŸàÿµŸäŸÑ</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrdersManagement;
