import React, { useEffect, useState } from "react";
import { supabase } from "../../utils/supabase";

/**
 * إدارة روابط التواصل الاجتماعي
 * - الفيسبوك، الإنستجرام، الواتس
 * - إضافة، تعديل، حذف الروابط
 */

const SocialLinksManagement = () => {
  const [links, setLinks] = useState([]);
  const [platform, setPlatform] = useState("");
  const [url, setUrl] = useState("");

  useEffect(() => {
    fetchLinks();
  }, []);

  const fetchLinks = async () => {
    const { data } = await supabase.from("social_links").select("*");
    setLinks(data || []);
  };

  const handleAdd = async () => {
    await supabase.from("social_links").insert([{ platform, url }]);
    setPlatform(""); 
    setUrl("");
    fetchLinks();
  };

  const handleDelete = async (id) => {
    await supabase.from("social_links").delete().eq("id", id);
    fetchLinks();
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">روابط التواصل الاجتماعي</h2>
      <div className="mb-4 flex flex-wrap gap-2">
        <input 
          placeholder="المنصة" 
          value={platform} 
          onChange={(e) => setPlatform(e.target.value)} 
          className="border p-2"
        />
        <input 
          placeholder="الرابط" 
          value={url} 
          onChange={(e) => setUrl(e.target.value)} 
          className="border p-2"
        />
        <button 
          onClick={handleAdd} 
          className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600"
        >
          إضافة
        </button>
      </div>
      <div className="space-y-2">
        {links.map(l => (
          <div key={l.id} className="border p-2 flex justify-between items-center">
            <span>{l.platform}: {l.url}</span>
            <button 
              onClick={() => handleDelete(l.id)} 
              className="bg-red-500 text-white px-2 rounded"
            >
              حذف
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SocialLinksManagement;
