import React, { useState, useEffect } from "react";
import Dashboard from "./Dashboard";
import ProductsManagement from "./ProductsManagement";
import CategoriesManagement from "./CategoriesManagement";
import ShippingManagement from "./ShippingManagement";
import OrdersManagement from "./OrdersManagement";
import { supabase } from "../../utils/supabase";

/**
 * ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
 * - ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿ™ÿ®ŸàŸäÿ®ÿßÿ™ (Dashboard, Products, Categories, Shipping, Orders)
 */

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [stats, setStats] = useState({ products: 0, categories: 0, orders: 0 });

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    const { count: products } = await supabase.from("products").select("*", { count: "exact", head: true });
    const { count: categories } = await supabase.from("categories").select("*", { count: "exact", head: true });
    const { count: orders } = await supabase.from("orders").select("*", { count: "exact", head: true });

    setStats({
      products: products || 0,
      categories: categories || 0,
      orders: orders || 0,
    });
  };

  const renderContent = () => {
    switch (activeTab) {
      case "products": return <ProductsManagement />;
      case "categories": return <CategoriesManagement />;
      case "shipping": return <ShippingManagement />;
      case "orders": return <OrdersManagement />;
      default: return <Dashboard stats={stats} />;
    }
  };

  return (
    <div className="flex min-h-screen">
      {/* ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ¨ÿßŸÜÿ®Ÿäÿ© */}
      <aside className="w-64 bg-gray-900 text-white p-4">
        <h1 className="text-2xl font-bold mb-6">ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ</h1>
        <ul className="space-y-4">
          <li className={`cursor-pointer ${activeTab === "dashboard" ? "font-bold text-pink-400" : ""}`} onClick={() => setActiveTab("dashboard")}>
            üè† ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
          </li>
          <li className={`cursor-pointer ${activeTab === "products" ? "font-bold text-pink-400" : ""}`} onClick={() => setActiveTab("products")}>
            üõí ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™
          </li>
          <li className={`cursor-pointer ${activeTab === "categories" ? "font-bold text-pink-400" : ""}`} onClick={() => setActiveTab("categories")}>
            üìÇ ÿßŸÑÿ£ŸÇÿ≥ÿßŸÖ
          </li>
          <li className={`cursor-pointer ${activeTab === "shipping" ? "font-bold text-pink-400" : ""}`} onClick={() => setActiveTab("shipping")}>
            üöö ÿßŸÑÿ¥ÿ≠ŸÜ
          </li>
          <li className={`cursor-pointer ${activeTab === "orders" ? "font-bold text-pink-400" : ""}`} onClick={() => setActiveTab("orders")}>
            üì¶ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™
          </li>
        </ul>
      </aside>

      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      <main className="flex-1 p-6 bg-gray-100">{renderContent()}</main>
    </div>
  );
};

export default AdminDashboard;
