import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useCartStore } from "../store/cartStore";
import supabase from "../utils/supabase";
import { notify } from "./ToastNotification";
import { FaFacebook, FaInstagram, FaWhatsapp } from "react-icons/fa";

const Header = () => {
  const cart = useCartStore((state) => state.cartItems);
  const addToCart = useCartStore((state) => state.addToCart);
  const clearCart = useCartStore((state) => state.clearCart);
  const [storeName, setStoreName] = useState("ÿßŸÑŸÖÿ™ÿ¨ÿ±");
  const [logo, setLogo] = useState(null);
  const [socialLinks, setSocialLinks] = useState({});
  const [showAbout, setShowAbout] = useState(false);
  const [showContact, setShowContact] = useState(false);

  useEffect(() => {
    const fetchSettings = async () => {
      const { data, error } = await supabase.from("settings").select("*").single();
      if (!error && data) {
        setStoreName(data.store_name || "ÿßŸÑŸÖÿ™ÿ¨ÿ±");
        setLogo(data.logo_url || null);
      }

      // ÿ¨ŸÑÿ® ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑÿ≥Ÿàÿ¥ŸäÿßŸÑ
      const { data: socialData } = await supabase.from("social_links").select("*").single();
      if (socialData) {
        setSocialLinks(socialData);
      }
    };
    fetchSettings();
  }, []);

  const cartCount = cart?.reduce((sum, item) => sum + item.quantity, 0) || 0;

  // ÿØÿßŸÑÿ© ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ ŸÖÿπ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÉŸÖŸäÿ©
  const handleAddToCart = (product) => {
    if (product.quantity <= 0) {
      notify("ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ± ÿ≠ÿßŸÑŸäÿßŸã", "warning");
      return;
    }
    addToCart({ ...product, maxQuantity: product.quantity });
    notify(`ÿ™ŸÖÿ™ ÿ•ÿ∂ÿßŸÅÿ© "${product.name}" ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©`, "success");
  };

  // ÿ™ŸÅÿ±Ÿäÿ∫ ÿßŸÑÿ≥ŸÑÿ©
  const handleClearCart = () => {
    clearCart();
    notify("ÿ™ŸÖ ÿ™ŸÅÿ±Ÿäÿ∫ ÿßŸÑÿ≥ŸÑÿ© ÿ®ŸÜÿ¨ÿßÿ≠", "info");
  };

  return (
    <>
      <header className="bg-pink-200 text-gray-900 p-4 flex justify-between items-center shadow-md">
        <Link
          to="/"
          className="flex items-center gap-3 hover:scale-105 transition-transform duration-200"
        >
          {logo ? (
            <img src={logo} alt="Logo" className="w-10 h-10 rounded-full" />
          ) : (
            <div className="w-10 h-10 bg-pink-400 rounded-full flex items-center justify-center text-white font-bold">
              üè¨
            </div>
          )}
          <h1 className="text-xl font-bold hover:text-pink-600 transition-colors duration-200">
            {storeName}
          </h1>
        </Link>

        <nav className="flex gap-6">
          <Link
            to="/"
            className="relative text-gray-900 hover:text-pink-600 transition-colors duration-200 after:absolute after:-bottom-1 after:left-0 after:w-0 after:h-0.5 after:bg-pink-600 after:transition-all after:duration-200 hover:after:w-full"
          >
            ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
          </Link>

          <Link
            to="/shop"
            className="relative text-gray-900 hover:text-pink-600 transition-colors duration-200 after:absolute after:-bottom-1 after:left-0 after:w-0 after:h-0.5 after:bg-pink-600 after:transition-all after:duration-200 hover:after:w-full"
          >
            ÿßŸÑŸÖÿ™ÿ¨ÿ±
          </Link>

          <button
            onClick={() => setShowAbout(true)}
            className="relative text-gray-900 hover:text-pink-600 transition-colors duration-200"
          >
            ŸÖŸÜ ŸÜÿ≠ŸÜ
          </button>

          <button
            onClick={() => setShowContact(true)}
            className="relative text-gray-900 hover:text-pink-600 transition-colors duration-200"
          >
            ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß
          </button>
        </nav>

        <div className="flex items-center gap-3">
          <Link
            to="/cart"
            className="flex items-center gap-2 bg-pink-500 text-white px-3 py-1 rounded-full hover:bg-pink-600 transition-colors duration-200 shadow-md"
          >
            <span>üõí</span>
            <span>ÿ≥ŸÑÿ©</span>
            <span className="bg-white text-pink-500 px-2 py-1 rounded-full text-sm font-bold">
              {cartCount}
            </span>
          </Link>

          {/* ÿ≤ÿ± ÿ™ŸÅÿ±Ÿäÿ∫ ÿßŸÑÿ≥ŸÑÿ© */}
          {cartCount > 0 && (
            <button
              onClick={handleClearCart}
              className="bg-red-500 text-white px-3 py-1 rounded-full hover:bg-red-600 transition-colors duration-200 shadow-md"
            >
              ÿ™ŸÅÿ±Ÿäÿ∫ ÿßŸÑÿ≥ŸÑÿ© üóëÔ∏è
            </button>
          )}
        </div>
      </header>

      {/* ŸÖŸàÿØÿßŸÑ ŸÖŸÜ ŸÜÿ≠ŸÜ */}
      {showAbout && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-2xl max-w-lg text-center shadow-lg">
            <h2 className="text-2xl font-bold text-pink-600 mb-4">ŸÖŸÜ ŸÜÿ≠ŸÜ</h2>
            <p className="text-gray-700 leading-relaxed">
              ÿßÿ≠ŸÜÿß <span className="font-bold text-pink-600">ŸÑÿßŸÉŸä ÿßÿ≥ÿ™Ÿàÿ±</span> üíñ  
              ŸÖÿ™ÿ¨ÿ± ÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸÖÿ™ÿÆÿµÿµ ŸÅŸä ÿ£ŸÜÿßŸÇÿ™ŸÉ Ÿàÿ±ÿßÿ≠ÿ™ŸÉ Ÿàÿ¨ŸÖÿßŸÑŸÉ.  
              ÿ®ŸÜŸáÿ™ŸÖ ÿ®ŸÉŸÑ ÿ™ŸÅÿµŸäŸÑÿ© ŸÖŸÜ ÿ£ŸàŸÑ <span className="font-semibold">ÿßŸÑÿØŸäÿ≤ÿßŸäŸÜ</span>  
              ŸÑŸÑŸÖÿßÿ™ÿ±ŸäÿßŸÑ ŸàŸÑÿ≠ÿØ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑŸÖŸÜÿ™ÿ¨.  
