import React, { useEffect, useState } from "react";
import { supabase } from "../../utils/supabase";

/**
 * إدارة الأقسام
 * - إضافة، تعديل، حذف الأقسام
 */

const CategoriesManagement = () => {
  const [categories, setCategories] = useState([]);
  const [name, setName] = useState("");

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    const { data } = await supabase.from("categories").select("*");
    setCategories(data || []);
  };

  const handleAddCategory = async () => {
    await supabase.from("categories").insert([{ name }]);
    setName("");
    fetchCategories();
  };

  const handleDelete = async (id) => {
    await supabase.from("categories").delete().eq("id", id);
    fetchCategories();
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">إدارة الأقسام</h2>
      <div className="mb-4">
        <input placeholder="اسم القسم" value={name} onChange={(e) => setName(e.target.value)} className="border p-2 mr-2"/>
        <button onClick={handleAddCategory} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">إضافة</button>
      </div>
      <div>
        {categories.map(c => (
          <div key={c.id} className="border p-2 mb-2 flex justify-between">
            <span>{c.name}</span>
            <button onClick={() => handleDelete(c.id)} className="bg-red-500 text-white px-2 rounded">حذف</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoriesManagement;
