import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useCartStore } from "../store/cartStore";
import supabase from "../utils/supabase";

const Header = () => {
  const cart = useCartStore((state) => state.cartItems);

  const [storeName, setStoreName] = useState("ÿßŸÑŸÖÿ™ÿ¨ÿ±");
  const [logo, setLogo] = useState(null);

  useEffect(() => {
    const fetchSettings = async () => {
      const { data, error } = await supabase.from("settings").select("*").single();
      if (!error && data) {
        setStoreName(data.store_name || "ÿßŸÑŸÖÿ™ÿ¨ÿ±");
        setLogo(data.logo_url || null);
      }
    };
    fetchSettings();
  }, []);

  const cartCount = cart?.reduce((sum, item) => sum + item.quantity, 0) || 0;

  return (
    <header className="bg-gray-800 text-white p-4 flex justify-between items-center shadow-md">
      {/* ÿ¥ÿπÿßÿ± Ÿàÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ± */}
      <Link to="/" className="flex items-center gap-3 hover:opacity-90 transition-opacity">
        {logo ? (
          <img src={logo} alt="Logo" className="w-10 h-10 rounded-full" />
        ) : (
          <div className="w-10 h-10 bg-gray-600 rounded-full flex items-center justify-center font-bold">
            üè¨
          </div>
        )}
        <h1 className="text-xl font-bold">{storeName}</h1>
      </Link>

      {/* ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© */}
      <nav className="flex gap-6">
        <Link to="/" className="hover:text-pink-400 transition-colors">ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</Link>
        <Link to="/shop" className="hover:text-pink-400 transition-colors">ÿßŸÑŸÖÿ™ÿ¨ÿ±</Link>
        <Link to="/about" className="hover:text-pink-400 transition-colors">ŸÖŸÜ ŸÜÿ≠ŸÜ</Link>
        <Link to="/contact" className="hover:text-pink-400 transition-colors">ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß</Link>
      </nav>

      {/* ÿ≥ŸÑÿ© */}
      <Link to="/cart" className="flex items-center gap-2 hover:scale-105 transition-transform">
        <span>üõí</span>
        <span>ÿ≥ŸÑÿ©</span>
        <span className="bg-red-500 text-white px-2 py-1 rounded-full text-sm">{cartCount}</span>
      </Link>
    </header>
  );
};

export default Header;
