// src/components/Header.jsx
import React from "react";
import { Link } from "react-router-dom";
import { useCartStore } from "../store/cartStore";

const Header = () => {
  const { cart } = useCartStore();

  const cartCount = cart?.length || 0;

  return (
    <header className="bg-pink-500 text-white shadow-md">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold">
          Lucky Store
        </Link>

        {/* Links */}
        <nav className="flex gap-4">
          <Link to="/" className="hover:text-gray-200">
            الرئيسية
          </Link>
          <Link to="/shop" className="hover:text-gray-200">
            المتجر
          </Link>
          <Link to="/about" className="hover:text-gray-200">
            من نحن
          </Link>
          <Link to="/contact" className="hover:text-gray-200">
            تواصل معنا
          </Link>
          <Link to="/admin" className="hover:text-gray-200">
            لوحة التحكم
          </Link>
        </nav>

        {/* Cart */}
        <Link to="/cart" className="relative">
          <span className="material-icons text-3xl">shopping_cart</span>
          {cartCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-white text-pink-600 text-xs font-bold px-2 py-0.5 rounded-full">
              {cartCount}
            </span>
          )}
        </Link>
      </div>
    </header>
  );
};

export default Header;
