// src/pages/Products.jsx
import React, { useEffect, useState } from "react";
import { useCartStore } from "../store/cartStore";
import supabase from "../utils/supabase";
import { notify } from "../components/ToastNotification";

export default function Products() {
  const [products, setProducts] = useState([]);
  const addToCart = useCartStore((state) => state.addToCart);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data, error } = await supabase.from("products").select("*");
    if (!error) {
      setProducts(data || []);
    }
  };

  const handleAddToCart = (product) => {
    if (product.quantity <= 0) {
      notify("ÿßŸÑŸÉŸÖŸäÿ© ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±ÿ© ÿ≠ÿßŸÑŸäÿßŸã", "error");
      return;
    }
    addToCart({ ...product, maxQuantity: product.quantity });
    notify(`ÿ™ŸÖÿ™ ÿ•ÿ∂ÿßŸÅÿ© "${product.name}" ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ©`, "success");
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6 text-center">üõçÔ∏è ŸÉŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™</h2>
      {products.length === 0 ? (
        <p className="text-center text-gray-500">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿ≠ÿßŸÑŸäÿßŸã</p>
      ) : (
        <div className="grid sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {products.map((product) => (
            <div
              key={product.id}
              className="border rounded-xl shadow hover:shadow-lg transition overflow-hidden bg-white flex flex-col"
            >
              {product.images && product.images.length > 0 ? (
                <img
                  src={product.images[0]}
                  alt={product.name}
                  className="h-48 w-full object-cover"
                />
              ) : (
                <div className="h-48 w-full bg-pink-100 flex items-center justify-center text-gray-400">
                  ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿµŸàÿ±ÿ©
                </div>
              )}

              <div className="p-4 flex flex-col flex-1">
                <h3 className="text-lg font-semibold">{product.name}</h3>
                <p className="text-pink-600 font-bold">{product.price} ÿ¨.ŸÖ</p>
                <p className="text-sm text-gray-500 flex-1 line-clamp-2">
                  {product.description}
                </p>
                <p className="text-xs text-gray-400 mt-1">
                  ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©: {product.quantity}
                </p>

                <button
                  onClick={() => handleAddToCart(product)}
                  disabled={product.quantity <= 0}
                  className={`mt-3 px-4 py-2 rounded-lg transition-colors ${
                    product.quantity > 0
                      ? "bg-pink-500 hover:bg-pink-600 text-white"
                      : "bg-gray-400 text-gray-700 cursor-not-allowed"
                  }`}
                >
                  {product.quantity > 0 ? "ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ© üõí" : "ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ ‚ùå"}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
