        <button
          onClick={() => setActiveTab('products')}
          className={`px-4 py-2 rounded ${activeTab === 'products' ? 'bg-pink-500 text-white' : 'bg-gray-200'}`}
        >
          üõç ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™
        </button>
        <button
          onClick={() => setActiveTab('categories')}
          className={`px-4 py-2 rounded ${activeTab === 'categories' ? 'bg-pink-500 text-white' : 'bg-gray-200'}`}
        >
          üìÇ ÿßŸÑÿ£ŸÇÿ≥ÿßŸÖ
