        {activeTab === 'categories' && <CategoriesManagement />}
        {activeTab === 'social' && <SocialLinksManagement />}
        {activeTab === 'shipping' && <ShippingManagement />}
      </div>
    </div>
  );
};

export default AdminDashboard;
