import React, { useEffect, useState } from "react";
import { supabase } from "../../supabase";

/**
 * ÿ•ÿØÿßÿ±ÿ© ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÖÿ™ÿ¨ÿ±
 * - ÿ™ÿπÿØŸäŸÑ ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ±
 * - ÿ™ÿπÿØŸäŸÑ ÿ¥ÿπÿßÿ± (ŸÑŸàÿ¨Ÿà) ÿßŸÑŸÖÿ™ÿ¨ÿ±
 */
const SettingsManagement = () => {
  const [storeName, setStoreName] = useState("");
  const [logo, setLogo] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    fetchSettings();
  }, []);

  // ÿ¨ŸÑÿ® ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿ©
  const fetchSettings = async () => {
    const { data, error } = await supabase.from("settings").select("*").single();
    if (data) {
      setStoreName(data.name || "");
      setLogo(data.logo || null);
      setPreview(data.logo || null);
    }
  };

  // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
  const handleSave = async () => {
    const { error } = await supabase
      .from("settings")
      .upsert({ id: 1, name: storeName, logo });

    if (error) {
      alert("‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™");
    } else {
      alert("‚úÖ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠");
      fetchSettings();
    }
  };

  // ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ÿµŸàÿ±ÿ© ÿ¨ÿØŸäÿØÿ©
  const handleLogoChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      setLogo(reader.result); // ŸÜÿÆÿ≤ŸÜ ÿßŸÑÿµŸàÿ±ÿ© Base64
      setPreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">‚öôÔ∏è ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÖÿ™ÿ¨ÿ±</h2>

      <div className="mb-4 flex flex-col gap-3">
        {/* ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ± */}
        <input
          type="text"
          placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ±"
          value={storeName}
          onChange={(e) => setStoreName(e.target.value)}
          className="border p-2 rounded"
        />

        {/* ÿßŸÑŸÑŸàÿ¨Ÿà */}
        <input type="file" accept="image/*" onChange={handleLogoChange} />
        {preview && (
          <img
            src={preview}
            alt="Preview"
            className="w-24 h-24 object-contain border rounded"
          />
        )}
      </div>

      <button
        onClick={handleSave}
        className="bg-pink-500 text-white px-6 py-2 rounded hover:bg-pink-600"
      >
        üíæ ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿ∫ŸäŸäÿ±ÿßÿ™
      </button>
    </div>
  );
};

export default SettingsManagement;
