import React, { useState } from "react";

// ŸÖŸÉŸàŸÜÿßÿ™ ÿßŸÑÿ•ÿØŸÖŸÜ
const Dashboard = () => <h2 className="text-xl">üìä ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™</h2>;
const Products = () => <h2 className="text-xl">üõí ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™</h2>;
const Orders = () => <h2 className="text-xl">üì¶ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™</h2>;

const Admin = () => {
  const [activeTab, setActiveTab] = useState("dashboard");

  const renderContent = () => {
    switch (activeTab) {
      case "products":
        return <Products />;
      case "orders":
        return <Orders />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="flex min-h-screen">
      {/* ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ¨ÿßŸÜÿ®Ÿäÿ© */}
      <aside className="w-64 bg-gray-900 text-white p-4">
        <h1 className="text-2xl font-bold mb-6">ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ</h1>
        <ul className="space-y-4">
          <li
            className={`cursor-pointer ${activeTab === "dashboard" ? "font-bold" : ""}`}
            onClick={() => setActiveTab("dashboard")}
          >
            üè† ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
          </li>
          <li
            className={`cursor-pointer ${activeTab === "products" ? "font-bold" : ""}`}
            onClick={() => setActiveTab("products")}
          >
            üõí ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™
          </li>
          <li
            className={`cursor-pointer ${activeTab === "orders" ? "font-bold" : ""}`}
            onClick={() => setActiveTab("orders")}
          >
            üì¶ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™
          </li>
        </ul>
      </aside>

      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      <main className="flex-1 p-6 bg-gray-100">{renderContent()}</main>
    </div>
  );
};

export default Admin;
