  const handleChange = (e) => {
    setLinks({ ...links, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    alert("‚úÖ ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ±Ÿàÿßÿ®ÿ∑!");
    // ŸáŸÜÿß ÿ™ŸÇÿØÿ± ÿ™ÿ≠ŸÅÿ∏ ÿßŸÑÿ±Ÿàÿßÿ®ÿ∑ ŸÅŸä Supabase
  };

  return (
    <div>
      <h2 className="text-xl mb-4">üîó ÿ•ÿØÿßÿ±ÿ© ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑÿ™ŸàÿßÿµŸÑ</h2>

      <div className="mb-4">
        <label>üåê ŸÅŸäÿ≥ÿ®ŸàŸÉ</label>
        <input
          type="text"
          name="facebook"
          value={links.facebook}
          onChange={handleChange}
          className="border p-2 w-full rounded"
        />
      </div>

      <div className="mb-4">
        <label>üì∏ ÿßŸÜÿ≥ÿ™ÿ¨ÿ±ÿßŸÖ</label>
        <input
          type="text"
          name="instagram"
          value={links.instagram}
          onChange={handleChange}
          className="border p-2 w-full rounded"
        />
      </div>

      <div className="mb-4">
        <label>üí¨ Ÿàÿßÿ™ÿ≥ÿßÿ®</label>
        <input
          type="text"
          name="whatsapp"
          value={links.whatsapp}
          onChange={handleChange}
          className="border p-2 w-full rounded"
        />
      </div>

      <button
        onClick={handleSave}
        className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600"
      >
        üíæ ÿ≠ŸÅÿ∏
      </button>
    </div>
  );
}
