import React, { useEffect, useState } from "react";
import { supabase } from "../../utils/supabase";
import { notify } from "../../components/ToastNotification";

/**
 * إدارة المنتجات في لوحة التحكم
 * - إضافة، تعديل، حذف المنتجات
 * - اختيار الصور من الجهاز
 * - تحديد الألوان والمقاسات والكمية والسعر
 * - اختيار القسم
 */

const ProductsManagement = () => {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);
  const [colors, setColors] = useState("");
  const [sizes, setSizes] = useState("");
  const [category, setCategory] = useState("");
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data } = await supabase.from("products").select("*");
    setProducts(data || []);
  };

  const handleAddProduct = async () => {
    const { data, error } = await supabase.from("products").insert([{
      name, description, price, quantity,
      colors: colors.split(","), sizes: sizes.split(","), category, images
    }]);

    if(error) return notify("حدث خطأ عند إضافة المنتج", "error");

    notify("تمت إضافة المنتج بنجاح", "success");
    fetchProducts();
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">إدارة المنتجات</h2>
      <div className="mb-4">
        <input placeholder="اسم المنتج" value={name} onChange={(e) => setName(e.target.value)} className="border p-2 mr-2"/>
        <input placeholder="الوصف" value={description} onChange={(e) => setDescription(e.target.value)} className="border p-2 mr-2"/>
        <input type="number" placeholder="السعر" value={price} onChange={(e) => setPrice(Number(e.target.value))} className="border p-2 mr-2"/>
        <input type="number" placeholder="الكمية" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} className="border p-2 mr-2"/>
        <input placeholder="الألوان (فاصلة)" value={colors} onChange={(e) => setColors(e.target.value)} className="border p-2 mr-2"/>
        <input placeholder="المقاسات (فاصلة)" value={sizes} onChange={(e) => setSizes(e.target.value)} className="border p-2 mr-2"/>
        <input placeholder="القسم" value={category} onChange={(e) => setCategory(e.target.value)} className="border p-2 mr-2"/>
        <button onClick={handleAddProduct} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">إضافة</button>
      </div>
      <div>
        {products.map(p => (
          <div key={p.id} className="border p-2 mb-2 flex justify-between">
            <span>{p.name}</span>
            <span>{p.price} جنيه</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsManagement;
