import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useCartStore } from "../store/cartStore";
import supabase from "../utils/supabase"; // üëà ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖŸÜ Supabase

const Header = () => {
  const cart = useCartStore((state) => state.cart);
  const [storeName, setStoreName] = useState("ÿßŸÑŸÖÿ™ÿ¨ÿ±");
  const [logo, setLogo] = useState(null);

  // üëà ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ŸÖŸÜ Supabase
  useEffect(() => {
    const fetchSettings = async () => {
      const { data, error } = await supabase.from("settings").select("*").single();

      if (!error && data) {
        setStoreName(data.store_name || "ÿßŸÑŸÖÿ™ÿ¨ÿ±");
        setLogo(data.logo_url || null);
      }
    };

    fetchSettings();
  }, []);

  const cartCount = cart?.reduce((sum, item) => sum + item.quantity, 0) || 0;

  return (
    <header className="bg-gray-800 text-white p-4 flex justify-between items-center">
      {/* ÿßŸÑÿ¥ÿπÿßÿ± Ÿàÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ± */}
      <Link to="/" className="flex items-center gap-3">
        {logo ? (
          <img src={logo} alt="Logo" className="w-10 h-10 rounded-full" />
        ) : (
          <div className="w-10 h-10 bg-gray-600 rounded-full flex items-center justify-center">
            üè¨
          </div>
        )}
        <h1 className="text-xl font-bold">{storeName}</h1>
      </Link>

      {/* ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© */}
      <nav className="flex gap-6">
        <Link to="/">ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</Link>
        <Link to="/shop">ÿßŸÑŸÖÿ™ÿ¨ÿ±</Link>
        <Link to="/about">ŸÖŸÜ ŸÜÿ≠ŸÜ</Link>
        <Link to="/contact">ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß</Link>
      </nav>

      {/* ÿßŸÑÿ≥ŸÑÿ© */}
      <Link to="/cart" className="flex items-center gap-2">
        <span>üõí</span>
        <span>ÿ≥ŸÑÿ©</span>
        <span className="bg-red-500 text-white px-2 py-1 rounded-full text-sm">
          {cartCount}
        </span>
      </Link>
    </header>
  );
};

export default Header;
