// src/components/ToastNotification.jsx
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const notify = (message, type = "info") => {
  switch (type) {
    case "success":
      toast.success(message);
      break;
    case "error":
      toast.error(message);
      break;
    default:
      toast(message);
  }
};

export default notify;
