    fetchLinks();
  }, []);

  return (
    <div className="container mx-auto p-6 text-center">
      <h2 className="text-3xl font-bold mb-6 text-pink-600">ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß</h2>
