// src/components/ToastNotification.jsx
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export const notify = {
  addToCart: (productName, quantity) =>
    toast.success(`✅ تم إضافة "${productName}" إلى السلة. الكمية: ${quantity}`),
  cartCleared: () => toast.info("🛒 تم تفريغ السلة."),
  orderConfirmed: () => toast.success("📦 تم تأكيد الطلب بنجاح."),
  outOfStock: (productName, available) =>
    toast.error(
      available > 0
        ? `⚠️ الكمية المطلوبة من "${productName}" غير متوفرة. الكمية المتاحة: ${available}`
        : `❌ المنتج "${productName}" غير متوفر حالياً`
    ),
  quantityUpdated: (productName, quantity) =>
    toast.info(`ℹ️ تم تحديث كمية "${productName}" في السلة. الكمية الآن: ${quantity}`),
};

export default notify;
