import React, { useState, useEffect } from "react";
import ProductCard from "../components/ProductCard";
import { useSettingsStore } from "../store/settingsStore";

// ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ¨ÿ±Ÿäÿ®Ÿäÿ© ŸÑÿ≠ÿØ ŸÖÿß ŸÜÿ±ÿ®ÿ∑ ÿ®ŸÇÿßÿπÿØÿ© ÿ®ŸäÿßŸÜÿßÿ™
const sampleProducts = [
  { id: 1, name: "ÿ∑ŸÇŸÖ ŸÑÿßŸÜÿ¨ÿ±Ÿä ÿ£ÿ≠ŸÖÿ±", price: 350, rating: 5, image: "/assets/sample1.jpg", category: "ŸÑÿßŸÜÿ¨ÿ±Ÿä" },
  { id: 2, name: "ÿ®Ÿäÿ¨ÿßŸÖÿ© ŸÇÿ∑ŸÜ", price: 250, rating: 4, image: "/assets/sample2.jpg", category: "ÿ®Ÿäÿ¨ÿßŸÖÿßÿ™" },
  { id: 3, name: "ÿ±Ÿàÿ® ÿ≥ÿßÿ™ÿßŸÜ", price: 400, rating: 5, image: "/assets/sample3.jpg", category: "ÿ±Ÿàÿ®ÿ≥" },
];

export default function Home() {
  const { categories } = useSettingsStore();
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("ÿßŸÑŸÉŸÑ");
  const [products, setProducts] = useState(sampleProducts);

  // ŸÅŸÑÿ™ÿ±ÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßŸÑÿ®ÿ≠ÿ´ + ÿßŸÑŸÇÿ≥ŸÖ
  const filteredProducts = products.filter((product) => {
    const matchSearch = product.name.toLowerCase().includes(search.toLowerCase());
    const matchCategory = selectedCategory === "ÿßŸÑŸÉŸÑ" || product.category === selectedCategory;
    return matchSearch && matchCategory;
  });

  return (
    <div className="space-y-6">
      {/* üîç ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´ */}
      <div className="flex justify-center">
        <input
          type="text"
          placeholder="ÿßÿ®ÿ≠ÿ´Ÿä ÿπŸÜ ŸÖŸÜÿ™ÿ¨..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border border-pink-300 rounded-xl px-4 py-2 w-1/2 focus:outline-pink-500"
        />
      </div>

      {/* üè∑Ô∏è ÿßŸÑÿ£ŸÇÿ≥ÿßŸÖ */}
      <div className="flex justify-center gap-3 flex-wrap">
        <button
          className={`px-4 py-2 rounded-full ${selectedCategory === "ÿßŸÑŸÉŸÑ" ? "bg-pink-500 text-white" : "bg-pink-200"}`}
          onClick={() => setSelectedCategory("ÿßŸÑŸÉŸÑ")}
        >
          ÿßŸÑŸÉŸÑ
        </button>
        {categories.map((cat) => (
          <button
            key={cat}
            className={`px-4 py-2 rounded-full ${selectedCategory === cat ? "bg-pink-500 text-white" : "bg-pink-200"}`}
            onClick={() => setSelectedCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* üõçÔ∏è ÿ¥ÿ®ŸÉÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ */}
      {filteredProducts.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {filteredProducts.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-600">ŸÑÿß ŸäŸàÿ¨ÿØ ŸÖŸÜÿ™ÿ¨ÿßÿ™ ŸÖÿ∑ÿßÿ®ŸÇÿ© ŸÑŸÑÿ®ÿ≠ÿ´</p>
      )}
    </div>
  );
}
