// src/pages/Products.jsx
import React from "react";
import { Link } from "react-router-dom";
import { useCartStore } from "../store/cartStore";

// ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ¨ÿ±Ÿäÿ®Ÿäÿ© (ŸÖŸÖŸÉŸÜ ÿ™ÿ±ÿ®ÿ∑Ÿáÿß ÿ®ŸÇÿßÿπÿØÿ© ÿ®ŸäÿßŸÜÿßÿ™ ŸÑÿßÿ≠ŸÇÿßŸã)
const sampleProducts = [
  {
    id: 1,
    name: "ÿ∑ŸÇŸÖ ŸÑÿßŸÜÿ¨ÿ±Ÿä ÿ£ÿ≠ŸÖÿ±",
    price: 350,
    rating: 5,
    image: "/assets/sample1.jpg",
  },
  {
    id: 2,
    name: "ÿ®Ÿäÿ¨ÿßŸÖÿ© ŸÇÿ∑ŸÜ",
    price: 250,
    rating: 4,
    image: "/assets/sample2.jpg",
  },
  {
    id: 3,
    name: "ÿ±Ÿàÿ® ÿ≥ÿßÿ™ÿßŸÜ ÿ£ŸÜŸäŸÇ",
    price: 400,
    rating: 5,
    image: "/assets/sample3.jpg",
  },
];

export default function Products() {
  const { addToCart } = useCartStore();

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold text-center mb-8">ŸÖŸÜÿ™ÿ¨ÿßÿ™ŸÜÿß üõçÔ∏è</h1>

      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3">
        {sampleProducts.map((product) => (
          <div
            key={product.id}
            className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition overflow-hidden flex flex-col"
          >
            {/* ÿµŸàÿ±ÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ */}
            <Link to={`/product/${product.id}`}>
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-56 object-cover"
              />
            </Link>

            {/* ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨ */}
            <div className="p-4 flex flex-col flex-1">
              <h2 className="text-lg font-semibold text-gray-800">
                {product.name}
              </h2>
              <p className="text-pink-600 font-bold mt-1">{product.price} ÿ¨.ŸÖ</p>

              {/* ÿßŸÑÿ™ŸÇŸäŸäŸÖ */}
              <div className="flex mt-2 text-yellow-400">
                {"‚≠ê".repeat(product.rating)}
                {"‚òÜ".repeat(5 - product.rating)}
              </div>

              <div className="mt-auto flex gap-2 pt-4">
                {/* ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨ */}
                <Link
                  to={`/product/${product.id}`}
                  className="flex-1 text-center bg-pink-100 text-pink-600 px-3 py-2 rounded-lg hover:bg-pink-200 transition"
                >
                  ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
                </Link>

                {/* ÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑÿ≥ŸÑÿ© */}
                <button
                  onClick={() => addToCart(product)}
                  className="flex-1 bg-pink-600 text-white px-3 py-2 rounded-lg hover:bg-pink-700 transition"
                >
                  üõí ÿ£ÿ∂ŸÅ
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
