import React from "react";

/**
 * الصفحة الرئيسية للوحة التحكم
 * - عرض إحصائيات بسيطة (عدد المنتجات، الأقسام، الطلبات)
 */

const Dashboard = ({ stats }) => {
  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">📊 الإحصائيات</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-pink-100 p-4 rounded shadow">
          <h3 className="text-lg font-semibold">🛒 المنتجات</h3>
          <p className="text-2xl">{stats.products || 0}</p>
        </div>
        <div className="bg-pink-100 p-4 rounded shadow">
          <h3 className="text-lg font-semibold">📂 الأقسام</h3>
          <p className="text-2xl">{stats.categories || 0}</p>
        </div>
        <div className="bg-pink-100 p-4 rounded shadow">
          <h3 className="text-lg font-semibold">📦 الطلبات</h3>
          <p className="text-2xl">{stats.orders || 0}</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
