import React, { useEffect, useState } from "react";
import supabase from "../../utils/supabase"; // ‚úÖ ÿ™ŸÖ ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÖÿ≥ÿßÿ± ŸàÿßŸÑÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ ÿßŸÑÿµÿ≠Ÿäÿ≠

const SettingsManagement = () => {
  const [storeName, setStoreName] = useState("");
  const [logoUrl, setLogoUrl] = useState("");
  const [logoFile, setLogoFile] = useState(null);

  // üëà ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿ©
  useEffect(() => {
    const fetchSettings = async () => {
      const { data, error } = await supabase.from("settings").select("*").single();
      if (!error && data) {
        setStoreName(data.store_name || "");
        setLogoUrl(data.logo_url || "");
      }
    };
    fetchSettings();
  }, []);

  // üëà ÿ±ŸÅÿπ ÿµŸàÿ±ÿ© ÿ¨ÿØŸäÿØÿ© ÿ•ŸÑŸâ ÿßŸÑÿ™ÿÆÿ≤ŸäŸÜ
  const uploadLogo = async () => {
    if (!logoFile) return null;

    const fileName = `logo_${Date.now()}.${logoFile.name.split(".").pop()}`;
    const { data, error } = await supabase.storage
      .from("logos")
      .upload(fileName, logoFile);

    if (error) {
      alert("ÿÆÿ∑ÿ£ ŸÅŸä ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ±ÿ©");
      return null;
    }

    const { data: urlData } = supabase.storage.from("logos").getPublicUrl(fileName);
    return urlData.publicUrl;
  };

  // üëà ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  const handleSave = async () => {
    let finalLogoUrl = logoUrl;

    if (logoFile) {
      const uploadedUrl = await uploadLogo();
      if (uploadedUrl) finalLogoUrl = uploadedUrl;
    }

    const { error } = await supabase
      .from("settings")
      .upsert({ id: 1, store_name: storeName, logo_url: finalLogoUrl });

    if (error) {
      alert("‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™");
    } else {
      setLogoUrl(finalLogoUrl);
      alert("‚úÖ ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠");
    }
  };

  return (
    <div className="p-6 bg-white shadow rounded-xl">
      <h2 className="text-2xl font-bold mb-4">ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÖÿ™ÿ¨ÿ±</h2>

      <div className="mb-4">
        <label className="block mb-1">ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ±:</label>
        <input
          type="text"
          value={storeName}
          onChange={(e) => setStoreName(e.target.value)}
          className="border p-2 w-full rounded"
        />
      </div>

      <div className="mb-4">
        <label className="block mb-1">ÿßŸÑŸÑŸàÿ¨Ÿà ÿßŸÑÿ≠ÿßŸÑŸä:</label>
        {logoUrl ? (
          <img src={logoUrl} alt="Logo" className="w-20 h-20 mb-2 rounded" />
        ) : (
          <p>ŸÑÿß ŸäŸàÿ¨ÿØ ŸÑŸàÿ¨Ÿà ÿ®ÿπÿØ</p>
        )}
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setLogoFile(e.target.files[0])}
          className="mt-2"
        />
      </div>

      <button
        onClick={handleSave}
        className="bg-blue-600 text-white px-4 py-2 rounded-lg"
      >
        üíæ ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
      </button>
    </div>
  );
};

export default SettingsManagement;
