import React, { useEffect, useState } from "react";
import supabase from "../utils/supabase";
import { useCartStore } from "../store/cartStore";
import { Link } from "react-router-dom";

const Home = () => {
  const [products, setProducts] = useState([]);
  const { addToCart, cartItems, increase, decrease } = useCartStore();

  useEffect(() => {
    const fetchProducts = async () => {
      const { data, error } = await supabase.from("products").select("*");
      if (!error && data) setProducts(data);
    };
    fetchProducts();
  }, []);

  // üîπ ÿØÿßŸÑÿ© ŸÑÿ±ÿ≥ŸÖ ÿßŸÑŸÜÿ¨ŸàŸÖ ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ŸÇŸäŸäŸÖ
  const renderStars = (rating) => {
    return [...Array(5)].map((_, i) => (
      <span key={i} className={i < rating ? "text-yellow-400" : "text-gray-300"}>
        ‚òÖ
      </span>
    ));
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6 text-center text-pink-600">
        ŸÖŸÜÿ™ÿ¨ÿßÿ™ŸÜÿß ÿßŸÑŸÖŸÖŸäÿ≤ÿ© üõçÔ∏è
      </h2>

      <div className="grid gap-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
        {products.map((product) => {
          const inCart = cartItems.find((item) => item.id === product.id);

          return (
            <div
              key={product.id}
              className="bg-white rounded-2xl shadow hover:shadow-lg transition-transform transform hover:-translate-y-1 overflow-hidden"
            >
              {/* ÿµŸàÿ±ÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ */}
              <Link to={`/product/${product.id}`}>
                <img
                  src={product.images?.[0] || "/assets/sample1.jpg"}
                  alt={product.name}
                  className="w-full h-56 object-cover"
                />
              </Link>

              <div className="p-4 space-y-2">
                {/* ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ™ÿ¨ */}
                <h3 className="font-bold text-lg text-gray-800 truncate">
                  {product.name}
                </h3>

                {/* ÿßŸÑÿ≥ÿπÿ± */}
                <p className="text-pink-600 font-semibold text-xl">
                  {product.price} ÿ¨.ŸÖ
                </p>

                {/* ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© */}
                <p className="text-sm text-gray-500">
                  ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©: {product.quantity} ŸÇÿ∑ÿπÿ©
                </p>

                {/* ÿßŸÑÿ™ŸÇŸäŸäŸÖ */}
                <div>{renderStars(product.rating || 4)}</div>

                {/* ÿ≤ÿ± ÿßŸÑÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑÿ≥ŸÑÿ© ÿ£Ÿà ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿ®ÿßŸÑŸÉŸÖŸäÿ© */}
                {inCart ? (
                  <div className="flex items-center gap-3 mt-3">
                    <button
                      onClick={() => decrease(product.id)}
                      className="bg-pink-500 text-white w-8 h-8 rounded-full flex items-center justify-center hover:bg-pink-600"
                    >
                      -
                    </button>
                    <span className="font-semibold">{inCart.quantity}</span>
                    <button
                      onClick={() => increase(product.id)}
                      className="bg-pink-500 text-white w-8 h-8 rounded-full flex items-center justify-center hover:bg-pink-600"
                    >
                      +
                    </button>
                  </div>
                ) : (
                  <button
                    onClick={() => addToCart(product)}
                    disabled={product.quantity <= 0}
                    className={`w-full mt-3 py-2 rounded-lg font-semibold transition-colors ${
                      product.quantity > 0
                        ? "bg-pink-600 text-white hover:bg-pink-700"
                        : "bg-gray-300 text-gray-600 cursor-not-allowed"
                    }`}
                  >
                    {product.quantity > 0 ? "ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ© üõí" : "ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±"}
                  </button>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Home;
