import React from "react";
import { Link } from "react-router-dom";
import { useCartStore } from "../store/cartStore";

// ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ¨ÿ±Ÿäÿ®Ÿäÿ© (ŸÖŸÖŸÉŸÜ ŸÑÿßÿ≠ŸÇÿßŸã ÿ™ÿ±ÿ®ÿ∑Ÿáÿß ŸÖŸÜ supabase ÿ£Ÿà API)
const sampleProducts = [
  {
    id: 1,
    name: "ÿ∑ŸÇŸÖ ŸÑÿßŸÜÿ¨ÿ±Ÿä ÿ£ÿ≠ŸÖÿ±",
    price: 350,
    rating: 5,
    image: "/assets/sample1.jpg",
    quantity: 5,
  },
  {
    id: 2,
    name: "ÿ®Ÿäÿ¨ÿßŸÖÿ© ŸÇÿ∑ŸÜ",
    price: 250,
    rating: 4,
    image: "/assets/sample2.jpg",
    quantity: 0,
  },
  {
    id: 3,
    name: "ÿ±Ÿàÿ® ÿØÿßŸÜÿ™ŸäŸÑ ÿ£ÿ≥ŸàÿØ",
    price: 400,
    rating: 5,
    image: "/assets/sample3.jpg",
    quantity: 3,
  },
];

const Shop = () => {
  const addToCart = useCartStore((state) => state.addToCart);

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-3xl font-bold mb-8 text-center text-pink-600">üõçÔ∏è ŸÖŸÜÿ™ÿ¨ÿßÿ™ŸÜÿß</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {sampleProducts.map((product) => (
          <div
            key={product.id}
            className="bg-white rounded-2xl shadow-lg overflow-hidden hover:scale-105 transition-transform duration-200"
          >
            <Link to={`/product/${product.id}`}>
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-64 object-cover"
              />
            </Link>
            <div className="p-4 space-y-2">
              <h3 className="text-lg font-bold text-gray-900">{product.name}</h3>
              <p className="text-pink-600 font-bold text-xl">{product.price} ÿ¨.ŸÖ</p>

              {/* ÿßŸÑÿ™ŸÇŸäŸäŸÖ */}
              <div className="flex text-yellow-400">
                {Array.from({ length: 5 }).map((_, idx) => (
                  <span key={idx}>{idx < product.rating ? "‚≠ê" : "‚òÜ"}</span>
                ))}
              </div>

              {/* ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© */}
              <p className="text-sm text-gray-600">
                {product.quantity > 0 ? (
                  <>ŸÖÿ™ÿßÿ≠: {product.quantity} ŸÇÿ∑ÿπÿ©</>
                ) : (
                  <span className="text-red-500 font-bold">ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±</span>
                )}
              </p>

              {/* ÿ≤ÿ± ÿßŸÑÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑÿ≥ŸÑÿ© */}
              <button
                onClick={() => {
                  if (product.quantity > 0) {
                    addToCart({ ...product, maxQuantity: product.quantity });
                  }
                }}
                disabled={product.quantity === 0}
                className={`w-full mt-3 px-4 py-2 rounded-lg font-semibold transition-colors ${
                  product.quantity > 0
                    ? "bg-pink-500 text-white hover:bg-pink-600"
                    : "bg-gray-400 text-gray-700 cursor-not-allowed"
                }`}
              >
                {product.quantity > 0 ? "ÿ•ÿ∂ÿßŸÅÿ© ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ© üõí" : "ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ ‚ùå"}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Shop;
