import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useCartStore } from "../store/cartStore";

// ุจูุงูุงุช ุชุฌุฑูุจูุฉ
const sampleProducts = [
  {
    id: 1,
    name: "ุทูู ูุงูุฌุฑู ุฃุญูุฑ",
    price: 350,
    rating: 5,
    image: "/assets/sample1.jpg",
    description: "ุทูู ูุงูุฌุฑู ุณุชุงู ุฃูุซูู ุจุชูุงุตูู ุฏุงูุชูู.",
    sizes: ["S", "M", "L"],
    colors: ["ุฃุญูุฑ", "ุฃุณูุฏ"],
  },
  {
    id: 2,
    name: "ุจูุฌุงูุฉ ูุทู",
    price: 250,
    rating: 4,
    image: "/assets/sample2.jpg",
    description: "ุจูุฌุงูุฉ ูุฑูุญุฉ ูู ุงููุทู ุงููุงุนู.",
    sizes: ["M", "L", "XL"],
    colors: ["ุฒูุฑู", "ุฃุจูุถ"],
  },
];

export default function ProductDetails() {
  const { id } = useParams();
  const product = sampleProducts.find((p) => p.id === Number(id));
  const { addToCart } = useCartStore();

  const [size, setSize] = useState(product?.sizes[0]);
  const [color, setColor] = useState(product?.colors[0]);

  if (!product) return <p className="text-center">ุงูููุชุฌ ุบูุฑ ููุฌูุฏ</p>;

  return (
    <div className="grid md:grid-cols-2 gap-6">
      <img src={product.image} alt={product.name} className="rounded-2xl shadow" />
      <div className="space-y-4">
        <h2 className="text-2xl font-bold">{product.name}</h2>
        <p className="text-pink-700 font-bold text-lg">{product.price} ุฌ.ู</p>
        <p>{product.description}</p>

        {/* ุงุฎุชูุงุฑ ุงูููุงุณ */}
        <div>
          <h4 className="font-semibold">ุงูููุงุณ:</h4>
          <div className="flex gap-2 mt-1">
            {product.sizes.map((s) => (
              <button
                key={s}
                onClick={() => setSize(s)}
                className={`px-3 py-1 rounded-lg border ${
                  size === s ? "bg-pink-500 text-white" : "bg-white"
                }`}
              >
                {s}
              </button>
            ))}
          </div>
        </div>

        {/* ุงุฎุชูุงุฑ ุงูููู */}
        <div>
          <h4 className="font-semibold">ุงูููู:</h4>
          <div className="flex gap-2 mt-1">
            {product.colors.map((c) => (
              <button
                key={c}
                onClick={() => setColor(c)}
                className={`px-3 py-1 rounded-lg border ${
                  color === c ? "bg-pink-500 text-white" : "bg-white"
                }`}
              >
                {c}
              </button>
            ))}
          </div>
        </div>

        <button
          onClick={() => addToCart({ ...product, size, color })}
          className="bg-pink-600 text-white px-6 py-2 rounded-lg hover:bg-pink-700"
        >
          ุฃุถู ุฅูู ุงูุณูุฉ ๐
        </button>
      </div>
    </div>
  );
}
