import React from "react";
import { Link } from "react-router-dom";
import { useSettingsStore } from "../store/settingsStore";

export default function Header() {
  const { storeName, logo } = useSettingsStore();

  return (
    <header className="bg-pink-200 shadow p-4 flex justify-between items-center">
      <h1 className="text-xl font-bold text-pink-900">{storeName}</h1>
      <img src={logo || "/logo.png"} alt="Logo" className="h-12 mx-auto" />
      <nav className="flex gap-4">
        <Link to="/" className="hover:text-pink-600">الرئيسية</Link>
        <Link to="/cart" className="hover:text-pink-600">السلة</Link>
        <Link to="/admin" className="hover:text-pink-600 text-sm">(لوحة التحكم)</Link>
      </nav>
    </header>
  );
}
