import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useCartStore } from "../store/cartStore";
import { supabase } from "../utils/supabase";

const Header = () => {
  const { cart } = useCartStore();
  const cartCount = cart.reduce((total, item) => total + item.quantity, 0);

  const [storeSettings, setStoreSettings] = useState({
    name: "Lucky Store",
    logo: null,
  });

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    const { data, error } = await supabase.from("settings").select("*").single();
    if (data) {
      setStoreSettings(data);
    }
  };

  return (
    <header className="bg-white shadow-md p-4 flex justify-between items-center">
      {/* ÿßŸÑŸÑŸàÿ¨Ÿà + ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ™ÿ¨ÿ± */}
      <Link to="/" className="flex items-center gap-2">
        {storeSettings.logo ? (
          <img src={storeSettings.logo} alt="Logo" className="w-10 h-10 object-contain" />
        ) : (
          <span className="w-10 h-10 bg-gray-300 flex items-center justify-center rounded-full">üõçÔ∏è</span>
        )}
        <span className="text-xl font-bold">{storeSettings.name}</span>
      </Link>

      {/* ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑŸÖŸàŸÇÿπ */}
      <nav className="flex gap-6">
        <Link to="/" className="hover:text-pink-500">ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</Link>
        <Link to="/shop" className="hover:text-pink-500">ÿßŸÑŸÖÿ™ÿ¨ÿ±</Link>
        <Link to="/about" className="hover:text-pink-500">ŸÖŸÜ ŸÜÿ≠ŸÜ</Link>
        <Link to="/contact" className="hover:text-pink-500">ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß</Link>
      </nav>

      {/* ÿ£ŸäŸÇŸàŸÜÿ© ÿßŸÑÿ≥ŸÑÿ© */}
      <Link to="/cart" className="relative flex items-center gap-1 hover:text-pink-500">
        <span>ÿ≥ŸÑÿ©</span>
        <span className="text-2xl">üõí</span>
        <span className="absolute -top-2 -right-3 bg-pink-500 text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
          {cartCount}
        </span>
      </Link>
    </header>
  );
};

export default Header;
