// src/pages/ProductDetails.jsx
import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useCartStore } from "../store/cartStore";

const sampleProducts = [
  {
    id: 1,
    name: "ÿ∑ŸÇŸÖ ŸÑÿßŸÜÿ¨ÿ±Ÿä ÿ£ÿ≠ŸÖÿ±",
    price: 350,
    rating: 5,
    image: "/assets/sample1.jpg",
    description: "ÿ∑ŸÇŸÖ ŸÑÿßŸÜÿ¨ÿ±Ÿä ÿ≥ÿ™ÿßŸÜ ÿ£ŸÜÿ´ŸàŸä ÿ®ÿ™ŸÅÿßÿµŸäŸÑ ÿØÿßŸÜÿ™ŸäŸÑ.",
    sizes: ["S", "M", "L"],
    colors: ["ÿ£ÿ≠ŸÖÿ±", "ÿ£ÿ≥ŸàÿØ"],
  },
  {
    id: 2,
    name: "ÿ®Ÿäÿ¨ÿßŸÖÿ© ŸÇÿ∑ŸÜ",
    price: 250,
    rating: 4,
    image: "/assets/sample2.jpg",
    description: "ÿ®Ÿäÿ¨ÿßŸÖÿ© ŸÖÿ±Ÿäÿ≠ÿ© ŸÖŸÜ ÿßŸÑŸÇÿ∑ŸÜ ÿßŸÑŸÜÿßÿπŸÖ.",
    sizes: ["M", "L", "XL"],
    colors: ["ÿ≤Ÿáÿ±Ÿä", "ÿ£ÿ®Ÿäÿ∂"],
  },
];

export default function ProductDetails() {
  const { id } = useParams();
  const product = sampleProducts.find((p) => p.id === Number(id));
  const { addToCart } = useCartStore();

  const [size, setSize] = useState(product?.sizes[0]);
  const [color, setColor] = useState(product?.colors[0]);

  if (!product) return <p className="text-center mt-10">‚ö†Ô∏è ÿßŸÑŸÖŸÜÿ™ÿ¨ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ</p>;

  return (
    <div className="p-6 grid md:grid-cols-2 gap-8 items-start">
      {/* ÿµŸàÿ±ÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ */}
      <div className="bg-white rounded-2xl shadow overflow-hidden">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-[400px] object-cover"
        />
      </div>

      {/* ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑŸÖŸÜÿ™ÿ¨ */}
      <div className="space-y-5">
        <h2 className="text-3xl font-bold text-gray-800">{product.name}</h2>
        <p className="text-pink-600 font-bold text-2xl">{product.price} ÿ¨.ŸÖ</p>
        <p className="text-gray-600">{product.description}</p>

        {/* ÿßŸÑŸÖŸÇÿßÿ≥ÿßÿ™ */}
        <div>
          <h4 className="font-semibold text-gray-700">ÿßŸÑŸÖŸÇÿßÿ≥:</h4>
          <div className="flex gap-2 mt-2">
            {product.sizes.map((s) => (
              <button
                key={s}
                onClick={() => setSize(s)}
                className={`px-4 py-2 rounded-lg border transition ${
                  size === s
                    ? "bg-pink-500 text-white border-pink-600"
                    : "bg-white text-gray-700 hover:bg-gray-100"
                }`}
              >
                {s}
              </button>
            ))}
          </div>
        </div>

        {/* ÿßŸÑÿ£ŸÑŸàÿßŸÜ */}
        <div>
          <h4 className="font-semibold text-gray-700">ÿßŸÑŸÑŸàŸÜ:</h4>
          <div className="flex gap-2 mt-2">
            {product.colors.map((c) => (
              <button
                key={c}
                onClick={() => setColor(c)}
                className={`px-4 py-2 rounded-lg border transition ${
                  color === c
                    ? "bg-pink-500 text-white border-pink-600"
                    : "bg-white text-gray-700 hover:bg-gray-100"
                }`}
              >
                {c}
              </button>
            ))}
          </div>
        </div>

        {/* ÿ≤ÿ± ÿßŸÑÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑÿ≥ŸÑÿ© */}
        <button
          onClick={() => addToCart({ ...product, size, color })}
          className="bg-pink-600 text-white px-6 py-3 rounded-xl hover:bg-pink-700 transition shadow-md"
        >
          ÿ£ÿ∂ŸÅ ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ© üõí
        </button>
      </div>
    </div>
  );
}
