import React, { useState } from "react";
import ProductsManagement from "./ProductsManagement";
import CategoriesManagement from "./CategoriesManagement";
import ShippingManagement from "./ShippingManagement";
import SettingsManagement from "./SettingsManagement"; // üëà ÿ•ÿ∂ÿßŸÅÿ© ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("products");

  const renderContent = () => {
    switch (activeTab) {
      case "products":
        return <ProductsManagement />;
      case "categories":
        return <CategoriesManagement />;
      case "shipping":
        return <ShippingManagement />;
      case "settings":
        return <SettingsManagement />; // üëà ŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
      default:
        return <h2>ÿßÿÆÿ™ÿ± ŸÖŸÜ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©</h2>;
    }
  };

  return (
    <div className="flex min-h-screen">
      {/* ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ¨ÿßŸÜÿ®Ÿäÿ© */}
      <aside className="w-64 bg-gray-900 text-white p-4">
        <h1 className="text-2xl font-bold mb-6">ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ</h1>
        <ul className="space-y-4">
          <li
            className={`cursor-pointer ${activeTab === "products" ? "font-bold" : ""}`}
            onClick={() => setActiveTab("products")}
          >
            üõí ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™
          </li>
          <li
            className={`cursor-pointer ${activeTab === "categories" ? "font-bold" : ""}`}
            onClick={() => setActiveTab("categories")}
          >
            üìÇ ÿßŸÑÿ£ŸÇÿ≥ÿßŸÖ
          </li>
          <li
            className={`cursor-pointer ${activeTab === "shipping" ? "font-bold" : ""}`}
            onClick={() => setActiveTab("shipping")}
          >
            üöö ÿßŸÑÿ¥ÿ≠ŸÜ
          </li>
          <li
            className={`cursor-pointer ${activeTab === "settings" ? "font-bold" : ""}`}
            onClick={() => setActiveTab("settings")}
          >
            ‚öôÔ∏è ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™
          </li>
        </ul>
      </aside>

      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      <main className="flex-1 p-6 bg-gray-100">{renderContent()}</main>
    </div>
  );
};

export default AdminDashboard;
