import React, { useState } from "react";
import ProductsManagement from "./admin/ProductsManagement";
import CategoriesManagement from "./admin/CategoriesManagement";
import SocialLinksManagement from "./admin/SocialLinksManagement";
import SettingsManagement from "./admin/SettingsManagement";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("settings");

  const renderContent = () => {
    switch (activeTab) {
      case "products":
        return <ProductsManagement />;
      case "categories":
        return <CategoriesManagement />;
      case "social":
        return <SocialLinksManagement />;
      case "settings":
      default:
        return <SettingsManagement />;
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* القائمة الجانبية */}
      <aside className="w-64 bg-gray-900 text-white flex flex-col">
        <div className="p-6 text-center border-b border-gray-700">
          <h2 className="text-2xl font-bold tracking-wide">لوحة التحكم</h2>
        </div>
        <nav className="flex-1 p-4">
          <ul className="space-y-2">
            <li>
              <button
                onClick={() => setActiveTab("settings")}
                className={`w-full flex items-center px-4 py-2 rounded-lg transition 
                  ${
                    activeTab === "settings"
                      ? "bg-gray-700 text-white"
                      : "text-gray-300 hover:bg-gray-800"
                  }`}
              >
                ⚙️ <span className="ml-2">إعدادات المتجر</span>
              </button>
            </li>
            <li>
              <button
                onClick={() => setActiveTab("social")}
                className={`w-full flex items-center px-4 py-2 rounded-lg transition 
                  ${
                    activeTab === "social"
                      ? "bg-gray-700 text-white"
                      : "text-gray-300 hover:bg-gray-800"
                  }`}
              >
                🌐 <span className="ml-2">روابط التواصل</span>
              </button>
            </li>
            <li>
              <button
                onClick={() => setActiveTab("products")}
                className={`w-full flex items-center px-4 py-2 rounded-lg transition 
                  ${
                    activeTab === "products"
                      ? "bg-gray-700 text-white"
                      : "text-gray-300 hover:bg-gray-800"
                  }`}
              >
                📦 <span className="ml-2">إدارة المنتجات</span>
              </button>
            </li>
            <li>
              <button
                onClick={() => setActiveTab("categories")}
                className={`w-full flex items-center px-4 py-2 rounded-lg transition 
                  ${
                    activeTab === "categories"
                      ? "bg-gray-700 text-white"
                      : "text-gray-300 hover:bg-gray-800"
                  }`}
              >
                🗂️ <span className="ml-2">إدارة الأقسام</span>
              </button>
            </li>
          </ul>
        </nav>
        <div className="p-4 border-t border-gray-700 text-sm text-gray-400 text-center">
          © 2025 متجر الملابس
        </div>
      </aside>

      {/* المحتوى الرئيسي */}
      <main className="flex-1 p-8 bg-white shadow-md rounded-l-3xl">
        {renderContent()}
      </main>
    </div>
  );
};

export default AdminDashboard;
