import React, { useState } from "react";
import SettingsManagement from "./SettingsManagement";
import SocialLinksManagement from "./SocialLinksManagement";
import ProductsManagement from "./ProductsManagement";
import CategoriesManagement from "./CategoriesManagement";

const ShippingManagement = () => {
  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">إدارة الشحن</h2>
      <p>هنا تقدر تضيف المحافظات مع أسعار الشحن الخاصة بيها.</p>
      {/* لاحقًا هنكمل الكود بربط Supabase */}
    </div>
  );
};

const AdminDashboard = () => {
  const [activeSection, setActiveSection] = useState("settings");

  const renderSection = () => {
    switch (activeSection) {
      case "settings":
        return <SettingsManagement />;
      case "social":
        return <SocialLinksManagement />;
      case "products":
        return <ProductsManagement />;
      case "categories":
        return <CategoriesManagement />;
      case "shipping":
        return <ShippingManagement />;
      default:
        return <SettingsManagement />;
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-800 text-white flex flex-col">
        <div className="p-4 text-2xl font-bold border-b border-gray-700">
          لوحة التحكم
        </div>
        <nav className="flex-1 p-2 space-y-2">
          <button
            onClick={() => setActiveSection("settings")}
            className={`w-full text-left px-3 py-2 rounded ${
              activeSection === "settings" ? "bg-gray-600" : "hover:bg-gray-700"
            }`}
          >
            الإعدادات (اسم + لوجو)
          </button>
          <button
            onClick={() => setActiveSection("social")}
            className={`w-full text-left px-3 py-2 rounded ${
              activeSection === "social" ? "bg-gray-600" : "hover:bg-gray-700"
            }`}
          >
            روابط التواصل
          </button>
          <button
            onClick={() => setActiveSection("products")}
            className={`w-full text-left px-3 py-2 rounded ${
              activeSection === "products" ? "bg-gray-600" : "hover:bg-gray-700"
            }`}
          >
            إدارة المنتجات
          </button>
          <button
            onClick={() => setActiveSection("categories")}
            className={`w-full text-left px-3 py-2 rounded ${
              activeSection === "categories" ? "bg-gray-600" : "hover:bg-gray-700"
            }`}
          >
            إدارة الأقسام
          </button>
          <button
            onClick={() => setActiveSection("shipping")}
            className={`w-full text-left px-3 py-2 rounded ${
              activeSection === "shipping" ? "bg-gray-600" : "hover:bg-gray-700"
            }`}
          >
            إدارة الشحن
          </button>
        </nav>
      </aside>

      {/* Main content */}
      <main className="flex-1 bg-gray-100 p-6 overflow-y-auto">
        {renderSection()}
      </main>
    </div>
  );
};

export default AdminDashboard;
