import React, { useState } from "react";
import { supabase } from "../../utils/supabase";

/**
 * إعدادات المتجر
 * - تعديل اسم المتجر والشعار
 * - يمكن تحميل صورة الشعار من الجهاز
 */

const SettingsManagement = () => {
  const [storeName, setStoreName] = useState("");
  const [logo, setLogo] = useState("");

  const handleSave = async () => {
    await supabase.from("settings").upsert([{ storeName, logo }]);
    alert("تم حفظ الإعدادات بنجاح");
  };

  const handleLogoChange = (e) => {
    const file = e.target.files[0];
    if(file){
      const reader = new FileReader();
      reader.onloadend = () => setLogo(reader.result);
      reader.readAsDataURL(file);
    }
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">إعدادات المتجر</h2>
      <div className="mb-4">
        <input placeholder="اسم المتجر" value={storeName} onChange={(e) => setStoreName(e.target.value)} className="border p-2 mr-2"/>
        <input type="file" onChange={handleLogoChange} className="border p-2"/>
        {logo && <img src={logo} alt="Logo" className="w-20 h-20 mt-2"/>}
        <button onClick={handleSave} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600 mt-2">حفظ</button>
      </div>
    </div>
  );
};

export default SettingsManagement;
