import React, { useState, useEffect } from "react";
import SettingsManagement from "./admin/SettingsManagement";
import SocialLinksManagement from "./admin/SocialLinksManagement";
import ProductsManagement from "./admin/ProductsManagement";
import CategoriesManagement from "./admin/CategoriesManagement";
import ShippingManagement from "./admin/ShippingManagement";
import { supabase } from "../utils/supabase";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("settings");
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    checkAdmin();
  }, []);

  // التحقق من كون المستخدم أدمن
  const checkAdmin = async () => {
    const user = supabase.auth.user();
    if (!user) return setIsAdmin(false);

    const { data } = await supabase
      .from("admins")
      .select("id")
      .eq("user_id", user.id)
      .single();

    setIsAdmin(!!data);
  };

  if (!isAdmin) {
    return <p className="text-center mt-20 text-xl">ليس لديك صلاحية دخول لوحة التحكم</p>;
  }

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4 text-center">لوحة تحكم الأدمن</h1>

      {/* تبويبات الإدارة */}
      <div className="flex gap-2 mb-4 justify-center flex-wrap">
        <button onClick={() => setActiveTab("settings")} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">الإعدادات</button>
        <button onClick={() => setActiveTab("social")} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">روابط التواصل</button>
        <button onClick={() => setActiveTab("products")} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">المنتجات</button>
        <button onClick={() => setActiveTab("categories")} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">الأقسام</button>
        <button onClick={() => setActiveTab("shipping")} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">الشحن</button>
      </div>

      {/* محتوى التبويب */}
      <div>
        {activeTab === "settings" && <SettingsManagement />}
        {activeTab === "social" && <SocialLinksManagement />}
        {activeTab === "products" && <ProductsManagement />}
        {activeTab === "categories" && <CategoriesManagement />}
        {activeTab === "shipping" && <ShippingManagement />}
      </div>
    </div>
  );
};

export default AdminDashboard;
