import React, { useEffect, useState } from "react";
import supabase from "../../utils/supabase"; // ✅ تم تعديل الاستيراد إلى default export

/**
 * إدارة المنتجات في لوحة التحكم
 * - إضافة، تعديل، حذف المنتجات
 * - اختيار الصور من الجهاز
 * - تحديد الألوان والمقاسات والكمية والسعر
 * - اختيار القسم
 */

const ProductsManagement = () => {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);
  const [colors, setColors] = useState("");
  const [sizes, setSizes] = useState("");
  const [category, setCategory] = useState("");
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data } = await supabase.from("products").select("*");
    setProducts(data || []);
  };

  const handleAddProduct = async () => {
    const { error } = await supabase.from("products").insert([{
      name, description, price, quantity,
      colors: colors.split(",").map(c => c.trim()), 
      sizes: sizes.split(",").map(s => s.trim()), 
      category, images
    }]);

    if(error){
      alert("حدث خطأ عند إضافة المنتج");
      return;
    }

    alert("تمت إضافة المنتج بنجاح");
    setName(""); setDescription(""); setPrice(0); setQuantity(0);
    setColors(""); setSizes(""); setCategory(""); setImages([]);
    fetchProducts();
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const readers = files.map(file => {
      return new Promise((resolve) => {
        const reader = new FileReader();
        reader.onloadend = () => resolve(reader.result);
        reader.readAsDataURL(file);
      });
    });

    Promise.all(readers).then(imgs => setImages(imgs));
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">إدارة المنتجات</h2>

      <div className="mb-4 flex flex-wrap gap-2">
        <input placeholder="اسم المنتج" value={name} onChange={(e) => setName(e.target.value)} className="border p-2"/>
        <input placeholder="الوصف" value={description} onChange={(e) => setDescription(e.target.value)} className="border p-2"/>
        <input type="number" placeholder="السعر" value={price} onChange={(e) => setPrice(Number(e.target.value))} className="border p-2"/>
        <input type="number" placeholder="الكمية" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} className="border p-2"/>
        <input placeholder="الألوان (فاصلة)" value={colors} onChange={(e) => setColors(e.target.value)} className="border p-2"/>
        <input placeholder="المقاسات (فاصلة)" value={sizes} onChange={(e) => setSizes(e.target.value)} className="border p-2"/>
        <input placeholder="القسم" value={category} onChange={(e) => setCategory(e.target.value)} className="border p-2"/>
        <input type="file" multiple onChange={handleImageChange} className="border p-2"/>
        <button onClick={handleAddProduct} className="bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">إضافة</button>
      </div>

      <div className="space-y-2 mt-4">
        {products.map(p => (
          <div key={p.id} className="border p-2 flex justify-between items-center">
            <div>
              <span className="font-bold">{p.name}</span> - {p.price} جنيه
            </div>
            <div>
              {/* يمكنك إضافة أزرار تعديل وحذف لاحقًا */}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsManagement;
