import React from "react";
import { Routes, Route, Link } from "react-router-dom";
import ProductsManagement from "./ProductsManagement";
import CategoriesManagement from "./CategoriesManagement";
import ShippingManagement from "./ShippingManagement";

const AdminDashboard = () => {
  return (
    <div className="flex">
      {/* القائمة الجانبية */}
      <aside className="w-1/4 bg-gray-100 p-4 h-screen">
        <nav className="space-y-2">
          <Link to="products" className="block p-2 bg-pink-200 rounded">إدارة المنتجات</Link>
          <Link to="categories" className="block p-2 bg-pink-200 rounded">إدارة الأقسام</Link>
          <Link to="shipping" className="block p-2 bg-pink-200 rounded">إدارة الشحن</Link>
        </nav>
      </aside>

      {/* منطقة المحتوى */}
      <main className="flex-1 p-4">
        <Routes>
          <Route path="products" element={<ProductsManagement />} />
          <Route path="categories" element={<CategoriesManagement />} />
          <Route path="shipping" element={<ShippingManagement />} />
          <Route path="*" element={<h2>اختر من القائمة</h2>} />
        </Routes>
      </main>
    </div>
  );
};

export default AdminDashboard;
