import React from "react";
import { Link } from "react-router-dom";
import { useCart } from "../store/cartStore";

const Header = () => {
  const { cartItems } = useCart();

  return (
    <header className="flex justify-between items-center p-4 bg-white shadow-md">
      <div className="flex items-center gap-4">
        <Link to="/" className="text-lg font-semibold hover:text-pink-500">الرئيسية</Link>
        <Link to="/cart" className="text-lg font-semibold hover:text-pink-500">
          السلة ({cartItems.length})
        </Link>
      </div>

      <div className="text-center">
        <Link to="/" className="inline-block">
          <img src="/assets/logo.png" alt="logo" className="h-12 mx-auto" />
        </Link>
      </div>

      <div className="flex items-center gap-4">
        <Link to="/admin/login" className="text-sm text-gray-700 hover:text-pink-500">
          تسجيل دخول الأدمن
        </Link>
      </div>
    </header>
  );
};

export default Header;
